    <?php
    // Établir une connexion à la base de données MySQL
    $servername = "localhost";
    $username = "root";
    $password = "";
    $dbname = "firebase";

    // Créer une connexion MySQLi
    $conn = new mysqli($servername, $username, $password, $dbname);

    // Vérifier la connexion
    if ($conn->connect_error) {
        die("Connexion échouée: " . $conn->connect_error);
    }

    // Exécuter une requête SQL pour récupérer les données
    $sql = "SELECT température FROM valeurs ORDER BY température DESC LIMIT 5";
    $result = $conn->query($sql);
    
    if ($result->num_rows > 0) {
        echo "<h3>température:</h3>";
        // Parcourir chaque ligne de résultat
        while ($row = $result->fetch_assoc()) {
            echo $row["température"] . "<br>";
        }
    } else {
        echo "0 résultats";
    }
    
    // Fermer la connexion MySQLi
    $conn->close();
    ?>
